mapper.select:
SELECT * FROM duo


        WHERE position = #{position } AND tier = #{tier}


        WHERE position = #{position }


        WHERE tier = #{tier}


ORDER BY memberId DESC

INSERT INTO duo (memberId, summonerName, position , tier, rank, most3, win, lose, latestWinRate, `desc`)
    values (#{memberId}, #{summonerName}, #{position}, #{tier}, #{rank}, #{most3}, #{win}, #{lose}, #{latestWinRate}, #{desc})
    RETURNING duoId;
